Player* player;
Map* level1Map;

#define MAP_OVERHEIGHT MAP_HEIGHT - SCREEN_HEIGHT

s16 mapShiftX = 0;
s16 mapShiftY = MAP_OVERHEIGHT;


void stateLevel1_tooglePause() {
    paused = !paused;
}

void stateLevel1_init() {
    stateLevel1_joyInit();

    stateLevel1_printMap();
    
    player = allocPlayer();
    player_init(player, 15, 144, MAP_OVERHEIGHT);
}

void stateLevel1_printMap() {
    PAL_setPalette(PAL0, level1_palette.data, DMA);
	VDP_loadTileSet(&level1_tileset, TILE_USER_INDEX, DMA);
	level1Map = MAP_create(&level1_map, GROUND_PLANE, TILE_ATTR_FULL(PAL0, FALSE, FALSE, FALSE, TILE_USER_INDEX));
    MAP_scrollTo(level1Map, 0, MAP_OVERHEIGHT);

    PAL_setColor(0, palette_grey); // ???????
    
    
    //for(s16 y = 0; y < MAP_HEIGHT; y++) {
	//	for(s16 x = 0; x < MAP_WIDTH; x++) {
	//		if (collisions1[y][x] == 0) {     
    //            VDP_drawImage(GROUND_PLANE, &grass, x, y);
    //        }
	//	}
	//}
    
}

void stateLevel1_update() {
    player_update(player, collisions1, MAP_WIDTH_TILES, MAP_HEIGHT_TILES, mapShiftX, mapShiftY);
    stateLevel1_scrollMap();

}

void stateLevel1_scrollMap() {
    bool scrollX = player_checkMapScrollX(player, mapShiftX, MAP_WIDTH);
    bool scrollY = player_checkMapScrollY(player, mapShiftY, MAP_HEIGHT);
    if (scrollX) {
        mapShiftX += player->velocity.x;
    } 
    if (scrollY) {
        mapShiftY += player->velocity.y;
    }
    if (scrollX || scrollY) {
        MAP_scrollTo(level1Map, mapShiftX, mapShiftY);
    }
}

void stateLevel1_release() {
    
}

void stateLevel1_joyHandlerBefore() {
    player->moving.x = DIRECTION_NONE;
    player->moving.y = DIRECTION_NONE;
    player->velocity.x = 0;
    player->velocity.y = 0;
}

void stateLevel1_joyHandlerAfter() {

}

void stateLevel1_buttonUp() {
    player->moving.y = DIRECTION_UP;

    if (!player->onCeiling) {
        player->velocity.y = -1;
    }
}

void stateLevel1_buttonDown() {
    player->moving.y = DIRECTION_DOWN;

    if (!player->onGround) {
        player->velocity.y = 1;
    }
}


void stateLevel1_buttonLeft() {
    player->moving.x = DIRECTION_LEFT;

    if (!player->onLeftStuff) {
        player->velocity.x = -1;
    }
}


void stateLevel1_buttonRight() {
    player->moving.x = DIRECTION_RIGHT;

    if (!player->onRightStuff) {
        player->velocity.x = 1;
    }
}

void stateLevel1_buttonStart() {
    stateLevel1_tooglePause();
}

